import React from 'react';
class App extends React.Component{
  constructor() {
    super();
    this.state = { value: "Welcome", name: "guys" };
  }
  componentWillMount() {
    alert("Learn JS");
  }
  render() {
    return <div>
      <h1>{this.state.value} {this.state.name}</h1>
      <button onClick={this.updatebtn}>Click</button>
      <button on onClick={this.deletebtn}>Delete Header</button>
    </div>
  }
  componentDidMount() {
    setTimeout(() => {
      this.setState({ value: "Thankyou" });
    }, 2000);
  }
  updatebtn = () => {
    this.setState({ value: "Love you" });
  }
  componentWillUpdate() {
    alert("Do you want to update?");
  }
  componentDidUpdate() {
    document.getElementById('mydiv').innerHTML = "UPDATED SUCCESSFULL!!!";
  }
  shouldComponentUpdate() {
    return true;
  }
  deletebtn = () => {
    this.setState({ value: false, name:false });
  }
  componentWillUnmount() {
    alert("Deleted!!!");
  }
  
 
 
  }

